filters.fragment:
precision highp float;
uniform sampler2D inputImageTexture;
varying vec2 textureCoordinate;
uniform float timeInfo;
void main()
{
vec2 newcoor = textureCoordinate;
float timediff = 1.0 - timeInfo;
if (textureCoordinate.y > timediff){
newcoor.y = textureCoordinate.y - timediff;
}else{
newcoor.y = 1.0 - (timediff - textureCoordinate.y);
}
vec4 video = texture2D(inputImageTexture, newcoor);
gl_FragColor = video;
}

precision highp float;
uniform sampler2D inputImageTexture;
varying vec2 textureCoordinate;
uniform float timeInfo;
void main()
{
vec2 newcoor = textureCoordinate;
if (textureCoordinate.y > timeInfo){
newcoor.y = textureCoordinate.y - timeInfo;
}
if (textureCoordinate.y > 0.5){
newcoor.y = textureCoordinate.y - 0.5;
}
vec4 video = texture2D(inputImageTexture, newcoor);
gl_FragColor = video;
}
